import { Button, DocumentLoadEvent, PdfJs, Position, PrimaryButton, Tooltip, Viewer } from '@react-pdf-viewer/core';
import { defaultLayoutPlugin } from '@react-pdf-viewer/default-layout';
import {
    HighlightArea,
    MessageIcon,
    RenderHighlightContentProps,
    RenderHighlightTargetProps,
    RenderHighlightsProps,
    highlightPlugin,
} from '@react-pdf-viewer/highlight';
import * as React from 'react';

interface Note {
    id: number;
    content: string;
    highlightAreas: HighlightArea[];
    previewImage: string;
    quote: string;
}

const IndexPage = () => {
    const [message, setMessage] = React.useState('');
    const [notes, setNotes] = React.useState<Note[]>([
        {
            id: 1,
            content: 'A simple text based note',
            highlightAreas: [
                {
                    height: 1.52844549202031,
                    left: 16.36300706077807,
                    pageIndex: 3,
                    top: 39.13215031098466,
                    width: 36.86193409940468,
                },
                {
                    height: 1.19617473288546,
                    left: 0,
                    pageIndex: 3,
                    top: 10.56621014048823,
                    width: 0,
                },
                {
                    height: 1.3259679981725108,
                    left: 16.36300706077807,
                    pageIndex: 3,
                    top: 40.848536451140625,
                    width: 9.779053241381698,
                },
                {
                    height: 1.3259679981725108,
                    left: 26.141492454658728,
                    pageIndex: 3,
                    top: 40.848536451140625,
                    width: 0.4914855323272878,
                },
                {
                    height: 1.52844549202031,
                    left: 26.515298352485118,
                    pageIndex: 3,
                    top: 40.71562814748668,
                    width: 20.483919640384883,
                },
            ],
            previewImage: '',
            quote: 'Configuring and administering a system for online  collaboration  using comment repositories',
        },
        {
            id: 2,
            content: 'A simple image based note',
            highlightAreas: [
                {
                    pageIndex: 4,
                    top: 43.6479,
                    left: 20.5386,
                    height: 1.86072,
                    width: 12.3162,
                },
            ],
            previewImage:
                'data:image/png;base64,',
            quote: '',
        },
    ]);
    const notesContainerRef = React.useRef<HTMLDivElement | null>(null);
    let noteId = notes.length;

    const noteEles: Map<number, HTMLElement> = new Map();
    const [currentDoc, setCurrentDoc] = React.useState<PdfJs.PdfDocument | null>(null);

    const handleDocumentLoad = (e: DocumentLoadEvent) => {
        setCurrentDoc(e.doc);
        if (currentDoc && currentDoc !== e.doc) {
            // User opens new document
            setNotes([]);
        }
    };

    const renderHighlightTarget = (props: RenderHighlightTargetProps) => (
        <div
            style={{
                background: '#eee',
                display: 'flex',
                position: 'absolute',
                left: `${props.selectionRegion.left}%`,
                top: `${props.selectionRegion.top + props.selectionRegion.height}%`,
                transform: 'translate(0, 8px)',
                zIndex: 2,
            }}
        >
            <Tooltip
                position={Position.TopCenter}
                target={
                    <Button onClick={props.toggle}>
                        <MessageIcon />
                    </Button>
                }
                content={() => <div style={{ width: '100px' }}>Add a note</div>}
            />
        </div>
    );

    const renderHighlightContent = (props: RenderHighlightContentProps) => {
        const addNote = () => {
            if (message !== '') {
                const note: Note = {
                    id: ++noteId,
                    content: message,
                    highlightAreas: props.highlightAreas,
                    previewImage: props.previewImage,
                    quote: props.selectedText,
                };
                setNotes(notes.concat([note]));
                props.cancel();
            }
        };

        return (
            <div
                style={{
                    background: '#fff',
                    border: '1px solid rgba(0, 0, 0, .3)',
                    borderRadius: '2px',
                    padding: '8px',
                    position: 'absolute',
                    left: `${props.selectionRegion.left}%`,
                    top: `${props.selectionRegion.top + props.selectionRegion.height}%`,
                    transform: 'translate(0, 8px)',
                    zIndex: 1,
                }}
            >
                <div>
                    <textarea
                        rows={3}
                        style={{
                            border: '1px solid rgba(0, 0, 0, .3)',
                        }}
                        onChange={(e) => setMessage(e.target.value)}
                    ></textarea>
                </div>
                <div
                    style={{
                        display: 'flex',
                        marginTop: '8px',
                    }}
                >
                    <div style={{ marginRight: '8px' }}>
                        <PrimaryButton onClick={addNote}>Add</PrimaryButton>
                    </div>
                    <Button onClick={props.cancel}>Cancel</Button>
                </div>
            </div>
        );
    };

    const jumpToNote = (note: Note) => {
        activateTab(3);
        const notesContainer = notesContainerRef.current;
        if (noteEles.has(note.id) && notesContainer) {
            notesContainer.scrollTop = noteEles.get(note.id).getBoundingClientRect().top;
        }
    };

    const renderHighlights = (props: RenderHighlightsProps) => (
        <div
            style={{
                position: 'absolute',
                top: 0,
                left: 0,
                height: '100%',
                width: '100%',
            }}
        >
            {notes.map((note) => {
                // Find the highlight areas belonging to the same page
                const highlightAreas = note.highlightAreas.filter(
                    (area) => area.pageIndex === props.pageIndex && area.width > 0,
                );
                if (!highlightAreas.length) {
                    return <React.Fragment key={note.id}></React.Fragment>;
                }

                const topArea = highlightAreas.sort((a, b) => a.top - b.top)[0];
                return (
                    <React.Fragment key={note.id}>
                        <div
                            style={Object.assign({}, props.getCssProperties(topArea, props.rotation), {
                                cursor: 'pointer',
                                // Position
                                right: '0',
                                transform: 'translate(50%, 0)',
                                zIndex: 2,
                                // Reset special properties created by `getCssProperties`
                                height: '',
                                left: '',
                                width: '',
                                // Center the content
                                alignItems: 'center',
                                display: 'flex',
                                justifyContent: 'center',
                                // Misc styles
                                background: '#fff',
                                border: '1px solid rgba(0, 0, 0, 0.2)',
                                borderRadius: '9999px',
                                padding: '0.25rem 0.5rem',
                            })}
                            title="Jump to note"
                            onClick={() => jumpToNote(note)}
                        >
                            <MessageIcon />
                        </div>
                        {highlightAreas.map((area, idx) => (
                            <div
                                key={idx}
                                style={Object.assign(
                                    {},
                                    {
                                        background: 'yellow',
                                        opacity: 0.3,
                                    },
                                    props.getCssProperties(area, props.rotation),
                                )}
                            />
                        ))}
                    </React.Fragment>
                );
            })}
        </div>
    );

    const highlightPluginInstance = highlightPlugin({
        renderHighlightTarget,
        renderHighlightContent,
        renderHighlights,
    });

    const { jumpToHighlightArea } = highlightPluginInstance;

    React.useEffect(() => {
        return () => {
            noteEles.clear();
        };
    }, []);

    const sidebarNotes = (
        <div
            ref={notesContainerRef}
            style={{
                overflow: 'auto',
                width: '100%',
            }}
        >
            {notes.length === 0 && <div style={{ textAlign: 'center' }}>There is no note</div>}
            {notes.map((note) => {
                return (
                    <div
                        data-testid={`note-${note.id}`}
                        key={note.id}
                        style={{
                            borderBottom: '1px solid rgba(0, 0, 0, .3)',
                            cursor: 'pointer',
                            padding: '8px',
                        }}
                        onClick={() => jumpToHighlightArea(note.highlightAreas[0])}
                        ref={(ref): void => {
                            noteEles.set(note.id, ref as HTMLElement);
                        }}
                    >
                        {note.previewImage ? (
                            <img
                                src={note.previewImage}
                                style={{
                                    height: 'auto',
                                    width: '100%',
                                }}
                            />
                        ) : (
                            <blockquote
                                style={{
                                    borderLeft: '2px solid rgba(0, 0, 0, 0.2)',
                                    fontSize: '.75rem',
                                    lineHeight: 1.5,
                                    margin: '0 0 8px 0',
                                    paddingLeft: '8px',
                                    textAlign: 'justify',
                                }}
                            >
                                {note.quote}
                            </blockquote>
                        )}
                        {note.content}
                    </div>
                );
            })}
        </div>
    );

    const defaultLayoutPluginInstance = defaultLayoutPlugin({
        sidebarTabs: (defaultTabs) =>
            defaultTabs.concat({
                content: sidebarNotes,
                icon: <MessageIcon />,
                title: 'Notes',
            }),
    });
    const { activateTab } = defaultLayoutPluginInstance;

    return (
        <div className="demo">
            <Viewer
                fileUrl="/pdf-open-parameters.pdf"
                plugins={[highlightPluginInstance, defaultLayoutPluginInstance]}
                onDocumentLoad={handleDocumentLoad}
            />
        </div>
    );
};

export default IndexPage;
